mapper.select:
SELECT 
FROM db_order
WHERE 1=1

	AND order_id = #{orderId}


	AND good_bar_code = #{goodBarCode}

SELECT 
FROM db_order
WHERE 1=1	

	AND create_time >= #{startDate}
	AND create_time <= #{endDate}
 

	AND company_code = #{companyCode}

ORDER BY create_time DESC

SELECT COUNT(0)
FROM db_order
WHERE 1=1

	AND create_time >= #{startDate}
	AND create_time <= #{endDate}
 

	AND company_code = #{companyCode}
